import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/grid'

const initialInformation = {
    firstName: '',
    lastName: '',
    email: ''
}

//initialInformation = initialInformation.replace(/\"([^(\")"]+)\":/g,"$1:");

export default function () {


    const useStyles = makeStyles(theme => ({
        root: {
          '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: 200,
          },
        },
      }));

    const classes = useStyles();

    const [values, setValues] = React.useState(initialInformation);
    const [list, setList] = useState([])

    const handleChange = event => {
        setValues({
            ...values,
            [event.target.name]: event.target.value
        } as any);

    };

    const handleOnSubmit = (event) => {
      event.preventDefault()
      setList([...list, values])
      }

    return ( 
        <form onSubmit={handleOnSubmit} className={classes.root} noValidate autoComplete="off">
        <Grid></Grid>
        <div>
          <TextField
            id="standard-multiline-flexible"
            label="First name"
            value={values.firstName}
            onChange={handleChange}
            name={'firstName'}
          />
          <TextField
            id="standard-textarea"
            label="Last name"
            placeholder="Placeholder"
            value={values.lastName}
            onChange={handleChange}
            name={'lastName'}
          />
           <TextField
            id="standard-textarea"
            label="Email"
            placeholder="Placeholder"
            value={values.email}
            onChange={handleChange}
            name={'email'}
          />
          <Button type="submit">save</Button>
          <Typography variant={'h6'}>
            {list.map((i) => {
              return (
                <li>
                  <Button> onClick={JSON.stringify(i)}} }</Button>
                </li>
              );
            }
            )}
          </Typography>
        </div>
      </form>
    )
}
