import React from 'react';
import Carousel from 'nuka-carousel';
import { makeStyles } from '@material-ui/core/styles';
import { NoSsr } from '@material-ui/core';

interface OwnProps {
    image: any;
  }

  type Props = OwnProps;

const useStyles = makeStyles(() => ({
    dot: {
        height: 25,
        width: 25,
        background: 'black',
        borderRadius: 50,

        '&.isCurrent': {background:'red!important'},
        '&:hover':{background:'blue'}
    },

    dots: {
        display: 'flex',
        flexDirection: 'row'
    }
}));

export default function ({image}: Props) {

    const classes = useStyles();
    return (
        <NoSsr>
            <Carousel
                renderBottomCenterControls={(props) => {
                    console.log(props.currentSlide);

                    let asiat = [];

                    for (let i = 0; i < props.slideCount; i++) { 
                        const isCurrent = props.currentSlide === i ? 'isCurrent' : ''
                        const className = `${isCurrent}  ${classes.dot} `
                        asiat.push(<div key={i} onClick={() => props.goToSlide(i)}
                        className={className}></div>)

                    }
                    return (
                        <div className={classes.dots}>
                            {asiat}
                        </div>
                    )
                }}
            >
                <img src="https://images.unsplash.com/photo-1587613725874-d9a1e8e23f6b?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=656&q=80" />
                <img src="https://images.unsplash.com/photo-1587613989342-7e21016caff3?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=668&q=80" />
                <img src="https://images.unsplash.com/photo-1522204523234-8729aa6e3d5f?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1350&q=80"/>
                <img src="https://via.placeholder.com/400/ffffff/c0392b/&text=slide6" />
            </Carousel>
        </NoSsr>
    );
}
