import React from 'react';
import { makeStyles } from "@material-ui/core/styles";
import {Grid, Box} from '@material-ui/core/';
import Button from '@material-ui/core/Button';
import DeleteIcon from '@material-ui/icons/Delete';


const useStyles = makeStyles((theme) => ({
  grid: {
     display:'flex',
     justifyContent: 'center',
     flexDirection: 'row',
  },
  image:{

  },
  information:{
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'flex-start',
  }

}));

const image = (person) => {
  if (person.image !== null) {
    return(
      <>
        <img src={person.image} width={200} />
      </>
    );
  }
  return (
    <>
    <img src={person.image} width={100}/>
    </>
  );
}

const Persons = ({ persons, onDelete }) => {

  function confirmDelete(id, name) {
    if(window.confirm(`Delete ${name}?`)) {
      onDelete(id);
    }
    return;
  }

  const classes = useStyles();
  return(
    <>
      {persons.map(person => (
        <li>
          <p key={person.id}>
          <Grid container={true} spacing={4} className={classes.grid}>
            <Grid item={true} className={classes.image} >
                {image(person)}
            </Grid>
            <Grid item={true} className={classes.information}>
              <Box>
                 {person.name} 
              </Box>
              <Box>
                 {person.number}
              </Box>
              <Button  
                onClick={() => confirmDelete(person.id, person.name)}
                >
                <DeleteIcon/>
              </Button>
            </Grid>
          </Grid>
          </p>
        </li>
      ))}
    </>
  );
}
export default Persons;
