import React, { useState } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';

const initialInformation = {
    firstName: '',
    lastName: '',
    email: ''
}

export default function () {

    const useStyles = makeStyles(theme => ({
        root: {
          '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: 200,
          },
        },
      }));

    const classes = useStyles();

    const [values, setValues] = React.useState(initialInformation);
    const [newValue, setNewValue] = useState('')
    const [showAll, setShowAll] = useState(true)

    const valuesToShow = showAll
      ? values: values;


    const handleChange = event => {
        setValues({
            ...values,
            [event.target.name]: event.target.value
        } as any);
        setNewValue(event.target.value)
    };

    const handleOnSubmit = (event) => {
      event.preventDefault()
      console.log(values)
        const valueObject = {
          firstName: '',
          lastName: '',
          email: ''
      }
      setValues(initialInformation)
      setNewValue('')
      }

    return ( 
        <form onSubmit={handleOnSubmit} className={classes.root} noValidate autoComplete="off">
        <div>
          <TextField
            id="standard-multiline-flexible"
            label="First name"
            value={values.firstName}
            onChange={handleChange}
            name={'firstName'}
          />
          <TextField
            id="standard-textarea"
            label="Last name"
            placeholder="Placeholder"
            value={values.lastName}
            onChange={handleChange}
            name={'lastName'}

          />
           <TextField
            id="standard-textarea"
            label="Email"
            placeholder="Placeholder"
            value={values.email}
            onChange={handleChange}
            name={'email'}
          />
          <input value={newValue}
                 onChange={handleChange}
                 />
          <Button type="submit">save</Button>
        </div>
          <Button onClick={() => setShowAll(showAll)}></Button>
      </form>


    )


}
