import React, { forwardRef } from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Button from '@material-ui/core/Button';
import Dialog from '@material-ui/core/Dialog';
import ListItemText from '@material-ui/core/ListItemText';
import ListItem from '@material-ui/core/ListItem';
import List from '@material-ui/core/List';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Grid from '@material-ui/core/Grid';
// import CloseIcon from '@material-ui/icons/Close';


import Slide from '@material-ui/core/Slide';
import { TransitionProps } from '@material-ui/core/transitions/transition';


const useStyles = makeStyles(theme => ({
  appBar: {
    position: 'relative',
  },
  title: {
    marginLeft: theme.spacing(2),
    flex: 1,
  },
  item:{

  },
  list:{
    paddingTop: 30,
    display: 'flex',
    justifyContent: 'center',
    listStyle: 'none',
  },
  dialog:{
    maxWidth: '1000'
  },
}));

const Transition: React.ComponentType<TransitionProps> = forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

interface OwnProps {
  list: any[];
  values: any;
}

type Props = OwnProps; 


export default function FullScreenDialog({ list, values }: Props) {
  const classes = useStyles();
  const [open, setOpen] = React.useState(false);


  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <div>
      <Button variant="outlined" color="primary" onClick={handleClickOpen}>
        Show Information
      </Button>
      <Dialog className={classes.dialog} open={open} onClose={handleClose} TransitionComponent={Transition}>
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton edge="start" color="inherit" onClick={handleClose} aria-label="close">
            {/*<CloseIcon />*/}
            </IconButton>
            <Typography variant="h6" className={classes.title}>
              My information
            </Typography>
            <Button autoFocus color="inherit" onClick={handleClose}>
              save
            </Button>
          </Toolbar>
        </AppBar>
        <List>
        <Typography variant={'h6'}>
            {list.map((i) => {
              return (
                <ul className={classes.list}>
                  <li>
                  <Grid item={true} className={classes.item}>
                    <TextField
                    id="standard-multiline-flexible"
                    label="First name"
                    value={values.firstName}
                    name={'firstName'}
                    />
                  </Grid>
                  <Grid item={true} className={classes.item} >
                  <TextField
                    id="standard-textarea"
                    label="Last name"
                    value={values.lastName}
                    name={'lastName'}
                  />
                  </Grid>
                  <Grid item={true} className={classes.item}>
                  <TextField
                    id="standard-textarea"
                    label="Email"
                    value={values.email}
                    name={'email'}
                  />
                  </Grid>
                  </li>
                </ul>
              );
            }
            )}
          </Typography>
        </List>
      </Dialog>
    </div>
  );
}
