import React from "react";
import Box from "@material-ui/core/Box";
import { Typography, makeStyles, Grid } from "@material-ui/core";


const useStyles = makeStyles((theme) => ({
    grid: {
       display:'flex',
       justifyContent: 'center',
       flexDirection: 'row',
    },
    image:{

    },
    information:{
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'flex-start',
      color: '#575757'
    },
    form:{

    }

  }));

interface OwnProps {
    persons: any[];
}

type Props = OwnProps;

function ShowContacts({ persons }: Props) {

  const classes = useStyles();

  const image = (person) => {
    if (person.image !== null) {
      return(
        <>
          <img src={person.image} width={200} style={{borderRadius: '70%'}} />
        </>
      );
    }
    return (
      <>
      <img src={'https://mcdowellhomes.com.au/wp-content/uploads/2016/09/no-user-image-300x300.gif'} width={200} style={{borderRadius: '70%'}}/>
      </>
    );
  }

  if (persons.length === 0){
  return (
    <div>
      <Box className={classes.form}>
        <Typography variant="h5" color="primary">
          Your Contacts
        </Typography>

        {persons.map((person) => (
          <li>
              <Grid container={true} spacing={6} className={classes.grid}>
                <Grid item={true} className={classes.image}>
                  {image(person)}
                </Grid>
                  {person.name}
                  {person.number}
              </Grid>
          </li>
        ))}
      </Box>
    </div>
  );
}
}

export default ShowContacts;
