import React, { useState } from "react";
import Box from "@material-ui/core/Box";
import { Typography, makeStyles, Grid } from "@material-ui/core";
import personService from "../components/ui/Persons";


const useStyles = makeStyles(() => ({
  wrapper: {},
  form: {
    textAlign: "center",
  },
}));

function ShowContacts() {
  const classes = useStyles();
  const [persons, setPersons] = useState([]);


  return (
    <div className={classes.wrapper}>
      <Box className={classes.form}>
        <Typography variant="h5" color="primary">
          Your Contacts
        </Typography>
        {persons.map((person) => (
          <li>
            <p key={person.id}>
              <Grid container={true} spacing={6} className={classes.grid}>
                <Grid item={true} className={classes.image}>
                  {image(person)}
                </Grid>
                <Grid item={true} className={classes.information}>
                  <Box>{person.name}</Box>
                  <Box>{person.number}</Box>
                  <Button onClick={() => confirmDelete(person.id, person.name)}>
                    <DeleteIcon color={"primary"} />
                  </Button>
                </Grid>
              </Grid>
            </p>
          </li>
        ))}
      </Box>
    </div>
  );
}

export default ShowContacts;
